import { test, expect } from '@playwright/test';

test('Handling No Results', async ({ page }) => {
  // navigate: Navigate to BookMyShow Bengaluru homepage
  await page.locator('https://in.bookmyshow.com/explore/home/bengaluru').navigate();

  // Fill input field
  await page.locator('input[type='search']').fill('NonExistentMovieTitle');

  // submit: Submit the search
  await page.locator('form[role='search']').submit();

  // verify: Verify that a 'no results' message or similar indicator is displayed
  await page.locator('div.no-results or p.no-results').verify();
});
