import { test, expect } from '@playwright/test';

test('Successful Movie Search', async ({ page }) => {
  // navigate: Navigate to BookMyShow Bengaluru homepage
  await page.locator('https://in.bookmyshow.com/explore/home/bengaluru').navigate();

  // Fill input field
  await page.locator('input[type='search']').fill('Coolie');

  // submit: Submit the search
  await page.locator('form[role='search']').submit();

  // verify: Verify that at least one result containing 'Coolie' or 'Coolie No. 1' is displayed
  await page.locator('div.movie-title:contains('Coolie No. 1') or div.movie-title:contains('Coolie')').verify();
});
