import { test, expect } from '@playwright/test';

test('Partial Match Search', async ({ page }) => {
  // navigate: Navigate to BookMyShow Bengaluru homepage
  await page.locator('https://in.bookmyshow.com/explore/home/bengaluru').navigate();

  // Fill input field
  await page.locator('input[type='search']').fill('Cool');

  // submit: Submit the search
  await page.locator('form[role='search']').submit();

  // verify: Verify that at least one result containing 'Coolie' (or similar partial matches) is displayed
  await page.locator('div.movie-title:contains('Coolie')').verify();
});
